let words=["PYTHON","PHP","JAVA","SCALA","JAVASCRIPT","ABACATE","BANANA"],answer="",chances=0,mistakes=0,corrects=0,rightLetter=[],displayWord="",step=0,currentStep=0;function checkGameOver(){answer.length==corrects?(setButtons(!0),document.getElementById("result").innerHTML="Você Venceu!!!",document.getElementById("result").style="color:#00FF88;"):mistakes==chances&&(setButtons(!0),document.getElementById("result").innerHTML="Você Perdeu!!!",document.getElementById("result").style="color:#FF00;")}function clickLetter(e){e.disabled=!0,answer.indexOf(e.value)>-1?(rightLetter.push(e.value),printWord()):(mistakes++,currentStep+=step,document.getElementById("mistakes").innerText=mistakes,updateFigure(currentStep)),checkGameOver()}function getKeyboard(){for(let e="A".charCodeAt();e<="Z".charCodeAt();e++){let t=document.createElement("input");t.type="button",t.value=String.fromCharCode(e),t.className="guess-letter",t.addEventListener("click",function(){clickLetter(this)}),document.getElementById("keyboard").appendChild(t)}}function getWord(){answer=words[Math.floor(Math.random()*words.length)],chances=Math.floor(1.5*answer.length),step=150/chances,document.getElementById("chances").innerText=chances,document.getElementById("mistakes").innerText=mistakes}function init(){answer="",chances=0,mistakes=0,corrects=0,rightLetter=[],currentStep=0,document.getElementById("result").innerHTML="",getWord(),printWord(""),updateFigure(0)}function reset(){init(),setButtons(!1)}function start(){getKeyboard(),init()}function setButtons(e){document.querySelectorAll(".guess-letter").forEach(t=>{t.disabled=e})}function printWord(){displayWord="",corrects=0;for(let e=0;e<answer.length;e++)rightLetter.indexOf(answer[e])>-1?(displayWord+=answer[e]+" ",corrects++):displayWord+="_ ";document.getElementById("word").innerText=displayWord}function updateFigure(e){document.getElementById("figure-container").style.height=`${e}px`}start();